import { useEffect, useRef, useState } from "react";
import "react-toastify/dist/ReactToastify.css";
import Header from "./components/Header";
import ContactList from "./components/ContactList";
import {
  getContacts,
  saveContact,
  udpatePhoto,
  deleteContact as apiDeleteContact,
} from "./api/ContactService";
import { Routes, Route, Navigate } from "react-router-dom";
import ContactDetail from "./components/ContactDetail";
import { toastError, toastSuccess } from "./api/ToastService";
import { ToastContainer } from "react-toastify";

function App() {
  const modalRef = useRef();
  const fileRef = useRef();
  const [data, setData] = useState({});
  const [currentPage, setCurrentPage] = useState(0);
  const [file, setFile] = useState(undefined);
  const [values, setValues] = useState({
    name: "",
    email: "",
    phone: "",
    address: "",
    title: "",
    status: "",
  });

  const getAllContacts = async (page = 0, size = 10) => {
    try {
      setCurrentPage(page);
      const { data } = await getContacts(page, size);
      setData(data);
      console.log(data);
    } catch (error) {
      console.log(error);
      toastError(error.message);
    }
  };

  const deleteContact = async (id) => {
    try {
      await apiDeleteContact(id);
      toastSuccess("Contact deleted successfully");
      getAllContacts(currentPage); // Refresh the contact list
    } catch (error) {
      console.log(error);
      toastError(error.message);
    }
  };

  const toggleModal = () => {
    modalRef.current.classList.toggle("open");
  };

  const addContact = async () => {
    try {
      await saveContact(values);
      toggleModal();
      toastSuccess("Contact added successfully");
      getAllContacts(currentPage);
    } catch (error) {
      console.log(error);
      toastError(error.message);
    }
  };

  const updateContact = async (id) => {
    try {
      // Implement your update logic here
      toastSuccess("Contact updated successfully");
      getAllContacts(currentPage);
    } catch (error) {
      console.log(error);
      toastError(error.message);
    }
  };

  const updateImage = async (id) => {
    try {
      // Implement your image update logic here
      toastSuccess("Image updated successfully");
      getAllContacts(currentPage);
    } catch (error) {
      console.log(error);
      toastError(error.message);
    }
  };

  useEffect(() => {
    getAllContacts();
  }, []);

  return (
    <>
      <Header toggleModal={toggleModal} nbOfContacts={data.totalElements} />
      <main className="main">
        <div className="container">
          <Routes>
            <Route path="/" element={<Navigate to={"/contacts"} />} />
            <Route
              path="/contacts"
              element={
                <ContactList
                  data={data}
                  currentPage={currentPage}
                  getAllContacts={getAllContacts}
                  deleteContact={deleteContact} // Pass the delete function
                />
              }
            />
            <Route
              path="/contacts/:id"
              element={
                <ContactDetail
                  updateContact={updateContact}
                  updateImage={updateImage}
                />
              }
            />
          </Routes>
        </div>
      </main>

      {/* Modal for adding contacts */}
      <div className="modal" ref={modalRef}>
        <div className="modal-content">
          <span className="close" onClick={toggleModal}>
            &times;
          </span>
          <h2>Add New Contact</h2>
          <input
            type="text"
            placeholder="Name"
            onChange={(e) => setValues({ ...values, name: e.target.value })}
          />
          <input
            type="email"
            placeholder="Email"
            onChange={(e) => setValues({ ...values, email: e.target.value })}
          />
          <input
            type="text"
            placeholder="Phone"
            onChange={(e) => setValues({ ...values, phone: e.target.value })}
          />
          <input
            type="text"
            placeholder="Address"
            onChange={(e) => setValues({ ...values, address: e.target.value })}
          />
          <input
            type="text"
            placeholder="Title"
            onChange={(e) => setValues({ ...values, title: e.target.value })}
          />
          <input
            type="text"
            placeholder="Status"
            onChange={(e) => setValues({ ...values, status: e.target.value })}
          />
          <button onClick={addContact}>Add Contact</button>
        </div>
      </div>

      <ToastContainer />
    </>
  );
}

export default App;
