// src/components/ContactList.js

import React, { useState, useEffect } from "react";
import { toastSuccess, toastError } from "../api/ToastService";
import { fetchContacts, deleteContact } from "../api/ContactService";

const ContactList = ({ onContactSelect }) => {
  const [contacts, setContacts] = useState([]);

  useEffect(() => {
    const getAllContacts = async () => {
      try {
        const data = await fetchContacts();
        setContacts(data);
      } catch (error) {
        console.error("Failed to fetch contacts:", error);
        toastError("Failed to fetch contacts");
      }
    };
    getAllContacts();
  }, []);

  const handleDeleteContact = async (id) => {
    const isConfirmed = window.confirm(
      "Are you sure you want to delete this contact?"
    );
    if (isConfirmed) {
      try {
        await deleteContact(id);
        setContacts((prevContacts) =>
          prevContacts.filter((contact) => contact.id !== id)
        );
        toastSuccess("Contact deleted successfully");
      } catch (error) {
        toastError("Failed to delete contact");
      }
    }
  };

  return (
    <ul>
      {contacts.map((contact) => (
        <li key={contact.id}>
          <h3 onClick={() => onContactSelect(contact.id)}>{contact.name}</h3>
          <button onClick={() => handleDeleteContact(contact.id)}>
            Delete
          </button>
        </li>
      ))}
    </ul>
  );
};

export default ContactList;
