import React from "react";
import { Link } from "react-router-dom";
import { deleteContact } from "../api/contactApi"; // Adjust the import according to your project structure

const Contact = ({ contact, onDelete }) => {
  const handleDelete = async () => {
    if (window.confirm("Are you sure you want to delete this contact?")) {
      try {
        await deleteContact(contact.id);
        onDelete(contact.id); // Notify parent to remove contact from state
      } catch (error) {
        console.error("Error deleting contact:", error);
        alert("Failed to delete contact.");
      }
    }
  };

  return (
    <div className="contact__item">
      <Link to={`/contacts/${contact.id}`} className="contact__header">
        <div className="contact__image">
          <img src={contact.photoUrl} alt={contact.name} />
        </div>
        <div className="contact__details">
          <p className="contact_name">{contact.name.substring(0, 15)}</p>
          <p className="contact_title">{contact.title}</p>
        </div>
      </Link>
      <div className="contact__body">
        <p>
          <i className="bi bi-envelope"></i> {contact.email.substring(0, 20)}
        </p>
        <p>
          <i className="bi bi-geo"></i> {contact.address}
        </p>
        <p>
          <i className="bi bi-telephone"></i> {contact.phone}
        </p>
        <p>
          {contact.status === "Active" ? (
            <i className="bi bi-check-circle"></i>
          ) : (
            <i className="bi bi-x-circle"></i>
          )}{" "}
          {contact.status}
        </p>
      </div>
      <button onClick={handleDelete} className="delete-button">
        Delete
      </button>
    </div>
  );
};

export default Contact;
